SELECT DISTINCT h.hitcount, s.name leverancier, c.name stad, g.name gemeente, d.name provincie FROM mhl_suppliers AS s INNER JOIN mhl_hitcount AS h ON s.id=h.supplier_ID INNER JOIN mhl_cities AS c ON s.City_ID=c.id INNER JOIN mhl_communes AS g ON c.commune_ID=g.id INNER JOIN mhl_districts AS d ON g.district_ID=d.id WHERE h.month = 1 AND h.year = '2014' AND (d.name = 'Limburg' OR d.name = 'Brabant' OR d.name = 'Zeeland')